mapper.delete:
UPDATE T_WX_USER_INFO SET
UPDATED_BY = #{updatedBy,jdbcType=VARCHAR},
UPDATED_AT = CURRENT_TIMESTAMP, DELETE_FLAG=1 WHERE id=#{id,jdbcType=INTEGER}

mapper.select:
	SELECT
	
	FROM T_WX_USER_INFO
	
		
		AND ID = #{id,jdbcType=INTEGER}
		
		
		AND USER_CODE = #{userCode,jdbcType=VARCHAR}
		
		
		AND USER_NAME = #{userName,jdbcType=VARCHAR}
		
		
		AND USER_TYPE = #{userType,jdbcType=VARCHAR}
		
		
		AND MOBILE = #{mobile,jdbcType=VARCHAR}
		
		
		AND EMAIL = #{email,jdbcType=VARCHAR}
		
		
		AND IS_FOLLOW = #{isFollow,jdbcType=VARCHAR}
		
		
		AND USER_STATUS = #{userStatus,jdbcType=VARCHAR}
		
		
		AND DELETE_FLAG = #{deleteFlag,jdbcType=TINYINT}
		
		
		AND CREATED_AT = #{createdAt,jdbcType=TIMESTAMP}
		
		
		AND CREATED_BY = #{createdBy,jdbcType=VARCHAR}
		
		
		AND UPDATED_AT = #{updatedAt,jdbcType=TIMESTAMP}
		
		
		AND UPDATED_BY = #{updatedBy,jdbcType=VARCHAR}
		

ORDER BY ID DESC
